import axios from 'axios';
import { ref } from 'vue';
import { showLoginForm } from '@/script/script';
const loginForm = ref();
const form = ref({
  username: '',
  password: ''
});
const loginRules = ref({
  username: [{
    required: true,
    message: '请输入用户名',
    trigger: 'blur'
  }],
  password: [{
    required: true,
    message: '请输入密码',
    trigger: 'blur'
  }]
});
function login() {
  loginForm.value.validate(valid => {
    if (valid) {
      axios.post("/user/login", this.loginForm).then(() => {
        showLoginForm.value = !showLoginForm.value;
      }).catch(() => {
        alert("用户名或密码错误");
      });
    } else {
      alert("请输入用户名和密码");
    }
  });
}
